import React, { Component } from 'react';
import CreatContent from './CreatContent';
import MyList from './MyList';
class Content extends Component {
	constructor() {
		super();
		this.state = {
			myList: MyList(), // my list dosyasından gelen veri

			counter: 0, // butonla 1 artan sayaç
			myNewList: [], // ekleme yapılacak liste
			durum: true // eleman kalmamışsa kalmedı yazdırmak için kullanılacaktır.
		};
		this.click = this.click.bind(this); // click metodunu bağlamak için
	}

	click() {
		if (this.state.counter < this.state.myList.length) {
			this.state.myNewList.push(this.state.myList[this.state.counter]);

			this.setState((prevState) => {
				return {
					counter: prevState.counter + 1
				};
			});
			this.viwedList = this.state.myNewList.map((tur) => (
				<CreatContent
					turName={tur.name}
					img={tur.img}
					country={tur.country}
					description={tur.description}
					theState={tur.theState}
				/>
			));
		} else {
			this.setState((prevState) => {
				return {
					durum: false
				};
			});
			return this.state.durum;
		}
	}

	render() {
		//constractor'den listeye ulaşma ve mapping adlı fonkisyona parametre olarak atama
		var theContent = this.state.myList;

		return (
			<div className="raw ">
				<button onClick={this.click} className="btn btn-info btn-lg btn-block">
					{' '}
					click me{' '}
				</button>

				{theContent}
				<div>
					{(() => {
						if (this.state.durum == false) {
							return <h5>Gösterilecek Tur kalmadı..</h5>;
						}
					})()}
				</div>
			</div>
		);
	}
}
export default Content;
