import React, { Component } from 'react';
import CreatContent from './CreatContent';
import SgkTurList from './lists/SgkTurList';
import DigerTurList from './lists/DigerTurList';
import KulturTurList from './lists/KulturTurList';
import KisTurList from './lists/KisTurList';
import EglenceTurList from './lists/EglenceTurList';
import MainContent from './MainContent';

export class Content extends Component {
	constructor() {
		super();
		this.state = {
			counterOfSgk: 0,
			counterOfKultur: 0,
			counterOfKis: 0,
			counterOfEglence: 0,
			counterOfDiger: 0,
			list: [],
			mainContent: <MainContent />,
			ktgrListes: [SgkTurList(), KulturTurList(), KisTurList(), EglenceTurList(), DigerTurList()]
		};
		this.sgk_tur_click = this.sgk_tur_click.bind(this); // click metodunu bağlamak için
		this.kultur_tur_click = this.kultur_tur_click.bind(this);
		this.kis_tur_click = this.kis_tur_click.bind(this);
		this.eglence_tur_click = this.eglence_tur_click.bind(this);
		this.diger_tur_click = this.diger_tur_click.bind(this);
	}
	// ***************************************************************************** SAYAÇ ARTTIRMA (Sonraki Düğmesi)
	increment = (ktgr) => {
		let i = 0;
		// console.log(this.state.ktgrListes[0].length); // sağlık ktgr uzunluk
		// console.log(this.state.ktgrListes[1].length); // kultur ktgr uzunluk
		// console.log(this.state.ktgrListes[2].length); // kış ktgr uzunluk
		// console.log(this.state.ktgrListes[3].length);
		// console.log(this.state.ktgrListes[4].length);

		// let turlar = this.state.ktgrListes.map((l) => {
		// 	[ l[0] ].map((t) => {
		// 		if (t.kategori === ktgr) {
		// 			this.setState((praveState) => {
		// 				counter: praveState.counter = t.id++;
		// 			});
		// 		}
		// 	});
		// });
		switch (ktgr) {
			case 'Saglik':
				if (
					this.state.ktgrListes[0].length != 0 &&
					this.state.counterOfSgk <= this.state.ktgrListes[0].length
				) {
					this.setState((praveState) => {
						counterOfSgk: praveState.counterOfSgk++;
					});
					let SaglikCounter=this.state.counterOfSgk;
					console.log('Sağlık',SaglikCounter);
				
				}
				break;
			case 'Kultur':
				if (
					this.state.ktgrListes[1].length != 0 &&
					this.state.counterOfKultur <= this.state.ktgrListes[1].length
				) {
					this.setState((praveState) => {
						counterOfKultur: praveState.counterOfKultur++;
					});
					console.log('Kültür', this.state.counterOfKultur);
				}
				break;

			case 'Kis':
				if (
					this.state.ktgrListes[2].length != 0 &&
					this.state.counterOfKis <= this.state.ktgrListes[2].length
				) {
					this.setState((praveState) => {
						counterOfKis: praveState.counterOfKis++;
					});
					console.log('Kış', this.state.counterOfKis);
				
				}
				break;
			case 'Eglence':
				if (
					this.state.ktgrListes[3].length != 0 &&
					this.state.counterOfEglence <= this.state.ktgrListes[3].length
				) {
					this.setState((praveState) => {
						counterOfEglence: praveState.counterOfEglence++;
					});
					console.log('Eğlence', this.state.counterOfEglence);
				}
				break;
			case 'Diger':
				if (
					this.state.ktgrListes[4].length != 0 &&
					this.state.counterOfDiger <= this.state.ktgrListes[4].length
				) {
					this.setState((praveState) => {
						counterOfDiger: ++praveState.counterOfDiger;
					});
					console.log('Diğer', this.state.counterOfDiger);
				}
				break;
		}
	};
	

	// Sağlık turlarından ilk elelman gösterme işlemi

	sgk_tur_click() {
		this.setState((praveState) => {
			return {
				List: SgkTurList
			};
		});

		this.viwedList = [ SgkTurList()[this.state.counterOfSgk]].map((tur) => (
			<CreatContent
				counter={tur.id}
				turName={tur.name}
				img={tur.img}
				country={tur.country}
				description={tur.description}
				theState={tur.theState}
				kategori={tur.kategori}
				increment={this.increment}
			/>
		));
		this.state.mainContent = this.viwedList;
	}
	// Kültür turlarından ilk elelman gösterme işlemi

	kultur_tur_click() {
		this.setState((praveState) => {
			return {
				List: KulturTurList
			};
		});

		this.viwedList = [ KulturTurList()[this.state.counterOfKultur] ].map((tur) => (
			<CreatContent
				counter={tur.id}
				turName={tur.name}
				img={tur.img}
				country={tur.country}
				description={tur.description}
				theState={tur.theState}
				kategori={tur.kategori}
				increment={this.increment}
			/>
		));
		this.state.mainContent = this.viwedList;
	}
	// Kış turlarından ilk elelman gösterme işlemi
	kis_tur_click() {
		// bu kod parçacığı sadece ekranda değişklik yapmamıza izin veriyor (state ulaştığı için)
		this.setState((praveState) => {
			return {
				List: KulturTurList
			};
		});

		this.viwedList = [ KisTurList()[this.state.counterOfKis] ].map((tur) => (
			<CreatContent
				counter={tur.id}
				turName={tur.name}
				img={tur.img}
				country={tur.country}
				description={tur.description}
				theState={tur.theState}
				kategori={tur.kategori}
				increment={this.increment}
			/>
		));
		this.state.mainContent = this.viwedList;
	}
	// Eğlence turlarından ilk elelman gösterme işlemi
	eglence_tur_click() {
		this.setState((praveState) => {
			return {
				List: KulturTurList
			};
		});

		this.viwedList = [ EglenceTurList()[this.state.counterOfEglence] ].map((tur) => (
			<CreatContent
				counter={tur.id}
				turName={tur.name}
				img={tur.img}
				country={tur.country}
				description={tur.description}
				theState={tur.theState}
				kategori={tur.kategori}
				increment={this.increment}
			/>
		));
		this.state.mainContent = this.viwedList;
	}
	// Diğer turlarından ilk elelman gösterme işlemi
	diger_tur_click() {
		this.setState((praveState) => {
			return {
				List: KulturTurList
			};
		});

		this.viwedList = [ DigerTurList()[this.state.counterOfDiger] ].map((tur) => (
			<CreatContent
				turName={tur.name}
				img={tur.img}
				country={tur.country}
				description={tur.description}
				theState={tur.theState}
				kategori={tur.kategori}
				increment={this.increment}
			/>
		));
		this.state.mainContent = this.viwedList;
	}

	render() {
		var theContent = this.state.mainContent;

		return (
			<div className="raw ">
				<ul className="nav justify-content-center bg-info btn-group rounded">
					<a className="text-white btn btn-info" onClick={this.sgk_tur_click}>
						<li>
							<b>Sağlık Turları</b>
						</li>
					</a>

					<a className="text-white btn btn-info" onClick={this.kultur_tur_click}>
						<li>
							<b>Kültür Turları {this.state.counterOfSgk}</b>
						</li>
					</a>

					<a className="text-white btn btn-info" onClick={this.kis_tur_click}>
						<li>
							<b>Kış Turları</b>
						</li>
					</a>

					<a className="text-white btn btn-info" onClick={this.eglence_tur_click}>
						<li>
							{' '}
							<b>Eğlence Turları</b>
						</li>
					</a>

					<a className="text-white btn btn-info" onClick={this.diger_tur_click}>
						<li>
							<b>Diğer Turlar</b>
						</li>
					</a>
				</ul>
				{theContent}
			</div>
		);
	}
}
export default Content;

