import React, { Component } from 'react';
import CreatContent from './CreatContent';
import SgkTurList from './lists/SgkTurList';

class Content extends Component {
	constructor() {
		super();
		this.state = {
			Counter: 0,
			list: [],
			newList: []
		};
		this.sgk_tur_click = this.sgk_tur_click.bind(this); // click metodunu bağlamak için
		this.kultur_tur_click = this.kultur_tur_click.bind(this);
		this.kis_tur_click = this.kis_tur_click.bind(this);
		this.eglence_tur_click = this.eglence_tur_click.bind(this);
		this.diger_tur_click = this.diger_tur_click.bind(this);
	}


	sgk_tur_click() {
		this.setState((praveState) => {
			return {
				List: SgkTurList
			};
		});

		if (this.sayac_plus_1()===0) {
			this.state.newList.push(SgkTurList()[0]);

			this.viwedList = this.state.newList.map((tur) => (
				<CreatContent
					turName={tur.name}
					img={tur.img}
					country={tur.country}
					description={tur.description}
					theState={tur.theState}
				/>
			));
			
		}
		console.log(this.sayac_plus_1());
	}
	sayac_plus_1(){
		this.setState(praveState=>{
			return {
				Counter: praveState.Counter++
			}
		})
		return this.state.Counter
	}
	kultur_tur_click() {
		this.setState((praveState) => {
			return {
				sonuc:'kultur'
			};
		});
	}
	kis_tur_click() {
		this.setState((praveState) => {
			return {
				sonuc: 'Kış'
			};
		});
	}
	eglence_tur_click() {
		this.setState((praveState) => {
			return {
				sonuc: 'Eğelence'
			};
		});
	}
	diger_tur_click() {
		this.setState((praveState) => {
			return {
				sonuc: 'Diğer'
			};
		});
	}

	render() {
		return (
			<div className="raw ">
				{/* <button onClick={this.click} className="btn btn-info btn-lg btn-block">
					{' '}
					click me{' '}
				</button> */}

				<ul className="nav justify-content-center bg-info btn-group rounded">
					<button className="text-white btn btn-info" onClick={this.sgk_tur_click}>
						<li>
							<b>Sağlık Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" onClick={this.kultur_tur_click}>
						<li>
							<b>Kültür Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" onClick={this.kis_tur_click}>
						<li>
							<b>Kış Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" onClick={this.eglence_tur_click}>
						<li>
							{' '}
							<b>Eğlence Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" onClick={this.diger_tur_click}>
						<li>
							<b>Diğer Turlar</b>
						</li>
					</button>
				</ul>
				{this.viwedList}
			</div>
		);
	}
}
export default Content;

