import React, { Component } from 'react';
import File from './File';
class App extends Component {
	constructor() {
		super();
		this.state = {
			number: 0
		};
	}
	clickHandler() {
    this.setState(
      prevState=> {
        return(
          {
            number: prevState.number + 1,
          }
        )
      }
    )
  }
	render() {
		return (
			<div>

				<h1>{this.state.number}</h1>
        <button onClick={this.clickHandler}> click </button>
			</div>
		);
	}
}

export default App;
