import React, { Component } from 'react';
import CreatContent from './CreatContent';
import MyList from './MyList';
class Content extends Component {
	constructor() {
		super();
		this.state = {};
		//	this.click = this.click.bind(this); // click metodunu bağlamak için
	}

	s_tur_click() {
		return <h1>sağlık</h1>;
	}
	render() {
		var theContent = 'asd';

		return (
			<div className="raw ">
				{/* <button onClick={this.click} className="btn btn-info btn-lg btn-block">
					{' '}
					click me{' '}
				</button> */}

				<ul className="nav justify-content-center bg-info btn-group rounded">
					<button className="text-white btn btn-info" onClick={this.s_tur_click}>
						<li>
		
							<b>Sağlık Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" href="#">
						<li>
							<b>Kültür Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" href="#">
						<li>
						
							<b>Kış Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info" href="#">
						<li>
							{' '}
							<b>Eğlence Turları</b>
						</li>
					</button>

					<button className="text-white btn btn-info">
						<li>
							
							<b>Diğer Turlar</b>
						</li>
					</button>
				</ul>
				{theContent}
			</div>
		);
	}
}
export default Content;


















